mapper.select:
select id,parent_id,user_nick_name,comment_content,comment_time,like_count,visible_type
from comment
where literature_id=#{literatureId} and parent_id=#{parentId}

    order by ${sortField} ${sortOrder}


    order by create_time asc

select id,parent_id,user_nick_name,comment_content,comment_time,like_count,visible_type from comment where literature_id=#{literatureId} and parent_id=#{parentId} and (visible_type=2 or user_id=#{userId})
order by ${sortField} ${sortOrder}
order by create_time asc

select id,parent_id,user_nick_name,comment_content,comment_time,like_count,visible_type
from comment
where parent_id=#{parentId}

    order by ${sortField} ${sortOrder}


    order by create_time asc

