mapper.select:
select literature.id, title, authors, journal, publish_time, download_count, final_score,score, group_concat(k.keyword_name separator ', ') as keywords from literature left join literature_score on literature.id = literature_score.literature_id and #{queryDTO.userId} = literature_score.user_id left join literature_keyword lk on literature.id = lk.literature_id left join keyword k on lk.keyword_id = k.id
and title like concat('%', #{queryDTO.title}, '%')
and publish_time >= #{queryDTO.publishTimeStart}
and publish_time <= #{queryDTO.publishTimeEnd}
and literature.id in ( select lk2.literature_id from literature_keyword lk2 where lk2.keyword_id in
#{keywordId}
)
group by literature.id
order by ${queryDTO.sortField} ${queryDTO.sortOrder}
order by publish_time desc

mapper.update:
update literature
set teacher_score_avg = #{avgScore}, teacher_score_count = #{count}, update_time = now()
where id = #{literatureId}

update literature
set student_score_avg = #{avgScore}, student_score_count = #{count}, update_time = now()
where id = #{literatureId}

update literature
set final_score = #{finalScore}, update_time = now()
where id = #{literatureId}
