mapper.select:
select sp.id, sp.name, sp.create_user_id, sp.create_user_nick_name, sp.type, sp.journal, sp.status, sp.remark, sp.submission_create_time, sp.create_time, sp.update_time
from submission_plan sp
left join submission_plan_user spu on sp.id = spu.plan_id

    spu.user_id = #{participantUserId}
    and sp.id = #{id}
    and sp.name like concat('%', #{name}, '%')
    and sp.create_user_id = #{createUserId}
    and sp.create_user_nick_name like concat('%', #{createUserNickName}, '%')
    and sp.type = #{type}
    and sp.status = #{status}

order by sp.create_time desc

mapper.update:
update submission_plan
name = #{name}, create_user_id = #{createUserId}, create_user_nick_name = #{createUserNickName}, type = #{type}, journal = #{journal}, status = #{status}, remark = #{remark}, submission_create_time = #{submissionCreateTime}, update_time = #{updateTime}
where id = #{id}
